<?phpnamespace App\Models;use Illuminate\Database\Eloquent\Model;class Ong extends Model{    protected $fillable = ['nome', 'cnpj', 'foto', 'telefone', 'uf', 'cidade', 'rua', 'cep', 'ranking', 'ativo', 'lat', 'lng'];    protected $table = 'ongs';    public static $rules = [        'nome' => 'required|max:250',        'cnpj' => 'required|max:14|unique:ongs',        'telefone' => 'required|max:12',        'uf' => 'required|max:2',        'cidade' => 'required|max:250',        'rua' => 'required|max:250',        'cep' => 'required',	   'lat' => 'required',        'lng' => 'required'    ];    public static $messages = [        'nome.required' => 'Informe o nome da ONG',        'nome.max' => 'O nome da ONG pode ter no máximo 250 caracteres',        'cnpj.required' => 'Informe o CNPJ da ONG',        'cnpj.max' => 'O CNPJ pode ter no máximo 14 caracteres',        'cnpj.unique' => 'O CNPJ já cadastrado',        'telefone.required' => 'Informe o telefone da ONG',        'telefone.max' => 'O telefone pode ter no máximo 12 caracteres',        'uf.required' => 'Informe a UF da ONG',        'uf.max' => 'A UF pode ter no máximo 2 caracteres',        'cidade.required' => 'Informe a cidade da ONG',        'cidade.max' => 'A cidade pode ter no máximo 250 caracteres',        'rua.required' => 'Informe a rua da ONG',        'rua.max' => 'A rua pode ter no máximo 250 caracteres',        'cep.required' => 'Informe o CEP da ONG',        'lat.required' => 'Informe a latitude',        'lng.required' => 'Informe a longitude'    ];    // Relationships    public function usuario()    {        return $this->hasOne('App\Models\Usuario');    }}